        fill: #fff;
    }

    .dark-theme .cu-simple-bar__row {
        background: var(--discord-background-secondary);
    }

    .dark-theme .cu-simple-bar__row .cu-accordion__toggle_expanded {
        background: var(--discord-background-secondary);
    }

    .dark-theme .cu-grid-sidebar__body {
        background: var(--discord-background-secondary);
    }

