    }

    .cu-nav-favorites-bar::before {
        height: 100%;
    }

    .cu-task-row-main__link-text-inner {
        font-size: 15px;
    }

    .dark-theme .cu-task-row__container {
        padding: 4px 0px;
    }
    .cu-subtasks-by-status-popup__block {
        color: #4e5bf2;
    }
    .cu-task-row__container {
        padding: 6px 0;
    }
    .cu-modal.cu-modal_md .cu-modal__dialog {
        width: calc(100vw - 280px);
    }
    .task-container {
        max-width: none;
        left: 72px;
        right: 72px;
        width: auto;
    }
    .cu-dashboard-doc-page_modal {
        width: unset !important;
    }
}

