    }

    .theme--dark .entryholder .u100Entry .entryBody .webfeeds-actions a,
    .theme--dark .entryholder .u100Entry .entryBody .webfeeds-actions a:hover,
    .theme--dark .sliderContainer .entryholder .u100Entry .entryBody .webfeeds-actions a,
    .theme--dark .sliderContainer .entryholder .u100Entry .entryBody .webfeeds-actions a:hover {
