    .theme--dark .OrganizeTable__toolbar-actions button {
        color: rgba(255, 255, 255, 0.4)!important;
        border: 1px solid rgba(255, 255, 255, 0.25)!important;
    }

    .theme--dark .secondary.loud:hover,
    .theme--dark .PriceTableColumn .secondary:hover,
    .theme--dark .OrganizeTable__toolbar-actions button:hover {
        color: rgba(255, 255, 255, 0.6)!important;
        border: 1px solid rgba(255, 255, 255, 0.3)!important;
    }

    .theme--dark #sideArea .fx.button.secondary,
    .theme--dark .col-xl-3.xlarge-only .fx.button.secondary,
    .theme--dark .sliderContainer .secondary:not(label):not(.danger):not(.accent):not(.full-width),
    .theme--dark .popover .secondary:not(label):not(.danger):not(.accent),
    .theme--dark .annotationsHolder .secondary {
        border: 1px solid rgba(255, 255, 255, 0.08)!important;
    }

    .theme--dark .page-deleteAccount .secondary,
    .theme--dark .modal.small .secondary,
    .theme--dark .modal.ConfirmDialog .secondary {
        border: 1px solid rgba(255, 255, 255, 0.3)!important;
    }

    .theme--dark a.button.secondary[title*="Continue with"],
    .theme--dark .leftBarWelcome .secondary,
    .theme--dark #feedlyChrome #searchBarFX .search-bar-right-col .button-login.secondary,
    .theme--dark .m-x-auto.secondary,
    .theme--dark #feedlyChrome .right-col .button-dropdown .secondary.button-icon-left:not(.danger):not(.accent):not(.small),
    .theme--dark #feedlyChrome .right-col .actions-container button.joined:not(:hover),
    .theme--dark .organize .actions-container button[data-uri="cortex"],
    .theme--dark #headerBarFX .header.row button[data-uri="cortex"],
    .theme--dark .organize .empty-state-actions button.secondary,
    .theme--dark .organize .OrganizeTable__row .OrganizeMetricCell__actions button,
    .theme--dark .page-account .actions-container button.secondary,
    .theme--dark #headerBarFX [href="https://feedly.com/i/pro/account"],
    .theme--dark .empty-state.is-sidebar .secondary,
    .theme--dark .flexLayout .secondary:not(label):not(.danger):not(.accent):not(.small),
    .theme--dark table .secondary:not(label):not(.danger):not(.accent):not(.small),
    .theme--dark .secondary[title="Organize sources"],
    .theme--dark .integrations .secondary,
    .theme--dark .secondary[href*="enterprise"],
    .theme--dark .billing .secondary:not(label),
    .theme--dark .field .full-width.container-flex.secondary {
        color: #AAAAAA!important;
        border: 1px solid #505050!important;
    }

    .leftBarWelcome .secondary,
    #box:not(.theme--dark) .empty-state.is-sidebar .button {
        color: #AAAAAA !important;
        border: 1px solid #505050 !important;
    }

    .leftBarWelcome .secondary:hover,
    #box:not(.theme--dark) .empty-state.is-sidebar .button:hover {
        color: #CCCCCC!important;
        border: 1px solid #585858!important;
    }

    .theme--dark .leftBarWelcome .secondary:hover,
    .theme--dark #feedlyChrome #searchBarFX .search-bar-right-col .button-login.secondary:hover,
    .theme--dark .m-x-auto.secondary:hover,
    .theme--dark #feedlyChrome .right-col .button-dropdown .secondary.button-icon-left:not(.danger):not(.accent):not(.small):hover,
    .theme--dark .organize .actions-container button[data-uri="cortex"]:hover,
    .theme--dark #headerBarFX .header.row button[data-uri="cortex"]:hover,
    .theme--dark .organize .empty-state-actions button.secondary:hover,
    .theme--dark .organize .OrganizeTable__row .OrganizeMetricCell__actions button:hover,
    .theme--dark .page-account .actions-container button.secondary:hover,
    .theme--dark #headerBarFX [href="https://feedly.com/i/pro/account"]:hover,
    .theme--dark .empty-state.is-sidebar .secondary:hover,
    .theme--dark .flexLayout .secondary:not(label):not(.danger):not(.accent):not(.small):hover,
    .theme--dark table .secondary:not(label):not(.danger):not(.accent):not(.small):hover,
    .theme--dark .secondary[title="Organize sources"]:hover,
    .theme--dark .integrations .secondary:hover,
    .theme--dark .secondary[href*="enterprise"]:hover,
    .theme--dark .billing .secondary:not(label):hover,
    .theme--dark .field .full-width.container-flex.secondary:hover {
        color: #CCCCCC!important;
        border: 1px solid #585858!important;
    }

    #box:not(.theme--dark) #feedlyPageFX.container.centered .header.row .following-feed,
    #box:not(.theme--dark) #feedlyPageFX.container.centered .small.following-feed {
        color: #757575!important;
        border: 1px solid #C7C7C7!important;
    }

    .theme--dark a.button.secondary[title*="Continue with"]:hover,
    .theme--dark a.button.secondary.enterprise:hover {
        border: 1px solid #808080!important;
    }

    .theme--dark .billing .danger-hover.secondary:hover {
        color: #F44336!important;
        border: 1px solid #F44336!important;
    }

    .fx .container {
        max-width: 1080px;
    }

    #headerBarFX header,
    #headerBarFX .row {
        max-width: 1080px;
    }
    .sliderWidth {
        width: 880px;
    }
    .theme--dark .TopHeaderBar {
        background-color: #2b343b !important;
        border-bottom-color: transparent !important;
    }
    .theme--dark #feedlyPageHeader.discoveryPage.fixedHeader,
    .theme--dark #headerBarFX,
    .theme--dark #headerBarFX .header.row,
    .theme--dark .OrganizeTable__toolbar.sticky.container {
        background-color: #2b343b !important;
        border-bottom-color: #1e272e !important;
    }

