/* ==UserStyle==
@namespace      USO Archive
@name           Feedly (dark + discord)
@author         nezhivar
@version        20220313.11.43
@description    Improve Feedly dark mode view
@license        No License
==/UserStyle== */
@-moz-document domain("feedly.com"),
domain("") {
    :root {
        font-family: "Open Sans" !important;
    }

    /* ==================== Body ==================== */
    #box:not(.theme--dark) #feedlyPageHolderFX,
    #box:not(.theme--dark) #feedlyFrame,
    .fx.fx .BeyondYourFeedlySearch__selector-button,
    .fx.fx .PriorityAndNotSelector__buttons-divider {
        background: rgba(54, 57, 63, 0.90) !important;
    }

    body.theme--dark:not(#intercom-container-body),
    body.theme--dark #feedlyFrame,
    body.theme--dark #feedlyPageHolderFX,
    body.theme--dark body.theme--dark #mainBarFX,
    body.theme--dark #feedlyPageFX.container.centered,
    body.theme--dark #feedlyPart0,
    .theme--dark .fx.fx .BeyondYourFeedlySearch__selector-button,
    .theme--dark .fx.fx .PriorityAndNotSelector__buttons-divider {
        background: rgba(54, 57, 63, 0.90) !important;
    }
    body {
        font-family: "Open Sans" !important;
        background: rgba(54, 57, 63, 0.90) !important;
    }
    .entryBody {
        font-family: "Open Sans" !important;
    }
    .feedlyChrome--leftnav-open {
        background: rgba(54, 57, 63) !important;
    }
    /* ==================== Login, Pro ==================== */
    .theme--dark #feedlyPageHolderFX.fx.page-landing #feedlyPageFX,
    .theme--dark #feedlyPageHolderFX.fx.page-landing .landing .header {
        background: #171B1F!important;
    }


    .theme--dark .landing-old .backgrounded.under-screenshot,
    .theme--dark .section-features {
        background: #171B1F!important;
        border-top-color: #171B1F!important;
    }

    .theme--dark #blackfriday-header::after {
        background: #2b343b !important;
    }

    .theme--dark .landing-old .passion.dark,
    .theme--dark #feedlyPageFX.container.centered .landing .background-accent {
        background: #272B2F!important;
        border-top-color: #111111!important;
        border-bottom-color: #111111!important;
    }

    .theme--dark .ProPage__hero::after {
        background-color: #40494F!important;
    }

    .theme--dark .col-xs-12.col-md-6 h1,
    .theme--dark #feedlyPageFX.container.centered .background-accent h2,
    .theme--dark .fx .page-pro-footer .list-people h1,
    .theme--dark .landing-old .section.top h1,
    .theme--dark .landing-old .passion.dark h1,
    .theme--dark .col-xs-12.col-md-6 div.sub,
    .theme--dark .landing-old .featureGrid .links b {
        color: #FFFFFF!important;
    }

    .theme--dark #feedlyPageHolderFX.fx.page-landing h2,
    .theme--dark .landing-old .backgrounded h1,
    .theme--dark .landing-old .backgrounded.under-screenshot h1,
    .theme--dark .fx .header.row h1,
    .theme--dark #feedlyPageHolderFX.fx.page-landing h2 div.sub {
        color: #DDDDDD!important;
    }

    .theme--dark .landing .kicker,
    .theme--dark .landing-old .backgrounded.under-screenshot .h1Intro,
    .theme--dark .landing-old .featureGrid .links a {
        color: #757575!important;
    }

    .theme--dark .fx .PriceTableColumn {
        background-color: #40494F!important;
        border-color: #202020!important;
        box-shadow: none!important;
    }

    .theme--dark .PriceTableColumn:nth-child(2) {
        border-top-color: #2BB24C!important;
    }

    .theme--dark .fx .PriceTableColumn .PriceTableColumn__header h2 {
        color: #050505!important;
    }

    .theme--dark .fx .feature .item-header,
    .theme--dark .fx .list-people .people .item-header,
    .theme--dark .landing-old .feature .label,
    .theme--dark .fx .PriceTableColumn .PriceTableColumn__header,
    .theme--dark .fx .PriceTableColumn .PriceTableColumn__features b {
        color: #DDDDDD!important;
    }

    .theme--dark .fx .feature .description,
    .theme--dark .fx .list-people .people .description,
    .theme--dark .landing-old .feature .description,
    .theme--dark .fx .PriceTableColumn PriceTableColumn__description,
    .theme--dark .fx .PriceTableColumn .PriceTableColumn__features {
        color: #BBBBBB!important;
    }

    .theme--dark #feedlyBlocked strong,
    .theme--dark .price-table-explaination {
        color: #999999!important;
    }

    /* ==================== Floating Panel, Title Bar ==================== */
    #box:not(.theme--dark) .ProfileBubble .picture.placeholder,
    #box:not(.theme--dark) .ProfileBubble .picture[style*='background-image'] {
        background-color: rgba(200, 200, 200, 0.4)!important;
    }

    .theme--dark .popover .ProfileBubble__menu menu,
    .theme--dark .header.row .right-col .button-dropdown .popover:not([style*="margin"]) .menu,
    .theme--dark .header.row .right-col .button-dropdown .popover:not([style*="margin"]) menu,
    .theme--dark .menu.menu-custom,
    .theme--dark #addContentPlaceholderFX .menu,
    .theme--dark .discover-search .popover,
    .theme--dark .form.add-url {
        background-color: #212121!important;
    }

    .theme--dark h2:not([data-uri*="category"]),
    .theme--dark .fx .profile-bubble-menu .user.item-with-visuals div,
    .theme--dark .fx .profile-bubble-menu menu li,
    .theme--dark .team-bubble-menu .item-header,
    .theme--dark table .item-header:not(.selected),
    .theme--dark .flexLayout .item-header,
    .theme--dark .container-flex .item-header,
    .theme--dark .user.item-with-visuals .item-header,
    .theme--dark .item-header.item-with-visuals,
    .theme--dark .mark-as-read-button-group.button.secondary .popover .item,
    .theme--dark .header.row .right-col .button-dropdown .menu .popover .secondary.label-radio,
    .theme--dark .header.row .right-col .button-dropdown .menu .secondary.label-radio.checked,
    .theme--dark .menu.menu-custom .item:not(.danger),
    .theme--dark #addContentPlaceholderFX .menu div,
    .theme--dark .discover-search .popover div,
    .theme--dark .popover .form.add-url label,
    .theme--dark .popover .form.add-url .item-header,
    .theme--dark #popupPart .popup.picker div {
        color: #FFFFFF!important;
    }

    .theme--dark .board-tag:not(:hover) .icon.delete-tag,
    .theme--dark .chip:not(:hover)::after,
    .theme--dark .item .value,
    .theme--dark .pageAction,
    .theme--dark .icon-secondary.icon-fx-search-marker-ios-xs,
    .theme--dark #feedlyPageHolderFX.fx.page-landing .landing-facet-visual img,
    .theme--dark [src="/images/login-feedly-logo@2x.png"],
    .theme--dark [src="//feedly.com/images/login-feedly-logo@2x.png"],
    .theme--dark [src="/images/login-devices@2x.png"],
    .theme--dark .empty-state.is-medium img:not([src="images/fx/illustrations/caught-up-v0.svg"]),
    .theme--dark .modal-notifications .badge.secondary,
    .theme--dark .modal-notifications .dots:not(.active),
    .theme--dark .p2-emptyLibrary.libraryPageWidth {
        -webkit-filter: grayscale(100%) invert(88%)!important;
        filter: grayscale(100%) invert(88%)!important;
    }

    .theme--dark .popover img[alt="Save to boards illustration"],
    .theme--dark [src="images/pro-upgrade-buffer.png"] {
        -webkit-filter: grayscale(100%) invert(88%) opacity(70%) brightness(70%)!important;
        filter: grayscale(100%) invert(88%) opacity(70%) brightness(70%)!important;
    }

    .theme--dark .empty-state.is-medium img[src="images/fx/illustrations/caught-up-v0.svg"] {
        -webkit-filter: opacity(70%)!important;
        filter: opacity(70%)!important;
    }

    .theme--dark .modal-notifications img,
    .theme--dark .SeoSignupModal__illustration {
        -webkit-filter: grayscale(100%) invert(100%) opacity(70%)!important;
        filter: grayscale(100%) invert(100%) opacity(70%)!important;
    }

    .theme--dark .search-bar-right-col .button-icon-only,
    .theme--dark .clear-input.button-icon-only {
        background-color: transparent!important;
    }

    #searchBoxPlaceholder {
        display: inline!important;
    }

    .hercule-search .DeluxeTextInputOverlay input {
        -webkit-transition: width 0.7s, color 0.4s!important;
        -moz-transition: width 0.7s, color 0.4s!important;
        -ms-transition: width 0.7s, color 0.4s!important;
        -o-transition: width 0.7s, color 0.4s!important;
        transition: width 0.7s, color 0.4s!important;
    }

    .theme--dark .hercule-search .DeluxeTextInputOverlay input,
    .theme--dark #searchBarFX .hercule-search .DeluxeTextInputOverlay input {
        background-color: #2b343b !important;
        color: #FFFFFF!important;
    }

    .theme--dark #searchBarFX .topic-list.menu.popover,
    .theme--dark #searchBoxPlaceholder .topic-list.menu.popover,
    .theme--dark .search-list.menu.popover {
        background-color: #1E2428!important;
    }
    .theme--dark .ShareBar {
        background-color: #1E2428!important;
        border-color: black !important;
    }
    .fx.fx.fx .floatingEntryContent__tab-button {
        opacity: .5;
    }

    .theme--dark #searchBarFX .topic-list.menu.popover .item,
    .theme--dark #searchBoxPlaceholder .topic-list.menu.popover .item,
    .theme--dark .search-list.menu.popover .item {
        color: #FFFFFF!important;
    }

    #topHeaderBarFX,
    #searchBarFX {
        background-color: #EFF0F2!important;
        box-shadow: 0 0 2px 0 #AAAAAA!important;
    }

    .theme--dark #topHeaderBarFX,
    .theme--dark #searchBarFX,
    .theme--dark #headerBar.fixed-bar {
        background-color: #1E2428!important;
        box-shadow: 1px 1px 1px 0px rgba(0, 0, 0, 0.25)!important;
    }

    .theme--dark #feedlyPageHeader.discoveryPage.fixedHeader,
    .theme--dark #headerBarFX,
    .theme--dark #headerBarFX .header.row,
    .theme--dark .OrganizeTable__toolbar.sticky.container {
        background-color: #3B3D3F!important;
        box-shadow: 1px 0 0 1px transparent!important;
    }

    #headerBarFX .header.row h1,
    #floatingTitleBar {
        color: #FC2103!important;
    }

    .theme--dark #headerBarFX .header.row h1,
    .theme--dark #floatingTitleBar {
        color: #FFFFFF!important;
    }

    #feedlyPageFX.container.centered .header.row .heading,
    #feedlyTitleBar {
        color: #032AFF!important;
    }

    .sliderContainer .header.sticky h1,
    .message.loading,
    .entriesLoadingMessage {
        color: #000000!important;
    }

    .theme--dark #feedlyPageFX.container.centered .header.row .heading,
    .theme--dark #feedlyTitleBar,
    .theme--dark .sliderContainer .header.sticky h1,
    .theme--dark .message.loading,
    .theme--dark .entriesLoadingMessage {
        color: #AAAAAA!important;
    }

    .sliderContainer .header.row.kickered .primary.button-icon-only[title="Mark as Read"] {
        box-shadow: none!important;
    }

    .theme--dark .sliderContainer .header.row.kickered .primary.button-icon-only[title="Mark as Read"] {
        background-color: transparent!important;
        border: none!important;
    }

    .mark-as-read-button-group.button.secondary,
    .button-dropdown button.no-border.secondary.button-icon-left,
    .button-dropdown button.secondary.button-icon-left[title=""] {
        border: 1px solid rgba(0, 0, 0, 0.15)!important;
    }


    .theme--dark .mark-as-read-button-group.button.secondary,
    .theme--dark .button-dropdown button.no-border.secondary.button-icon-left,
    .theme--dark .button-dropdown button.secondary.button-icon-left[title=""] {
        border: 1px solid rgba(0, 0, 0, 0.3)!important;
    }

    .mark-as-read-button span,
    .header.row .count-decoration {
        color: #757575!important;
    }

    .theme--dark .feedUnreadCountHint {
        color: #555555!important;
        font-weight: bold!important;
