/* ==UserStyle==
@name           Clean Stackoverflow
@namespace      USO Archive
@author         Guillaume Massé
@description    `Keep only Q&A`
@version        20190303.21.8
@license        CC0-1.0
@preprocessor   uso
==/UserStyle== */
@document domain("stackexchange.com"), domain("stackoverflow.com") {
#content {
  margin: 5px;
  width: 100%;
  border: none;
}

#mainbar, .mainbar {
  width: 100%;
}

body {
    padding-top:0;
}

.community-option,
#sidebar,
#left-sidebar,
header,
.bottom-notice,
#footer {
    display: none;
}
}

